import React, { useState, useContext, Fragment, useEffect } from 'react';
import { Link } from 'react-router-dom';
import AlertaContext from '../../context/alertas/alertaContext';
import AuthContext from '../../context/autentificacion/authContext';

//import { Dropdown } from 'semantic-ui-react';
//import { makeStyles } from '@material-ui/core/styles';
//import TextField  from '@material-ui/core/TextField';
//import MenuItem from '@material-ui/core/MenuItem';
//import classes from '*.module.css';

/*
const opciones = [
    {
        value: 'Sernageomin',
        label: '$',
    },
    {
        value: 'Supervisor',
        label: 'B',
    },
];

const useStyles = makeStyles((theme) => ({
    root: {
        '& .MuiTextField-root': {
            margin: theme.spacing(1),
            width: '25ch',
        },
    },
})); 
*/

const Registro = (props) => {

    //extraer los valores del context
    //use context permite acceder a todas las funciones y al state
    const alertaContext = useContext(AlertaContext);
    const { alerta, mostrarAlerta } = alertaContext;

    const authContext = useContext(AuthContext);
    const { mensaje, autenticado, registrarUsuario } = authContext;


    //En caso de que el usuario se haya autenticado o registrado o sea
    //un registro duplicado
    useEffect(() => {
        if(autenticado) {
            //aca redijiremos al usuario autenticad a la pag de proyectos
            props.history.push('/proyectos');
        }

        if(mensaje) {
            mostrarAlerta('Todos lo campos son obligatorios');
        }
        // eslint-disable-next-line
    }, [/*mensaje,*/ autenticado, props.history])

    //State para iniciar sesiÃ³n
    //los states tiene que tener el mismo nombre que name ej name=nombre
    const [usuario, guardarUsuario] = useState({
        rut: '',
        nombre: '',
        apellido: '',
        ciudad: '',
        telefono: '',
        cargo: '',
        email:'',
        password: '',
        confirmar: ''
    });

    //extraer de usuario
    const { rut, nombre, apellido, ciudad, telefono, cargo, email, password, confirmar } = usuario;

    //aqui vamos a ir guardando en el State
    const onChange = e => {
        guardarUsuario({
            //traemos la copia de usuario para lo que este en el State no se 
            //sobre escriba en otra pieza de esta
            //para reescribir la actual -> [e.target.name] : e.target.value
            ...usuario,
            [e.target.name] : e.target.value
        });
    }

    //Cuando el usuario quiere iniciar sesion
    const onSubmit = e => {
        e.preventDefault();

        //Validar que no haya campos vacios
        if( rut.trim() === '' || nombre.trim() === '' || apellido.trim() === '' || ciudad.trim() === '' ||
        telefono.trim() === '' || cargo.trim() === '' || email.trim() === '' || password.trim() === '' || 
        confirmar.trim() === '' ) {
            mostrarAlerta('Todos los campos son obligatorios');
            return;
        }

        //Password minimo de 6 caracteres
        if(password.length < 6) {
            mostrarAlerta('El password debe ser de al menos 6 caracteres', 'alerta-error');
            return;
        }
        //Los dos passwors son iguales
        if(password !== confirmar) {
            mostrarAlerta('Los passwords no son iguales');
            return;
        }

        //Pasarlo al 
        registrarUsuario({
            rut,
            nombre,
            apellido,
            ciudad,
            telefono,
            cargo, 
            email,
            password
        });
    }

    /*
    const cityOptions = [
        { key: 'ar', value: 'ar', flag: 'ar', text: 'Arica' },
        { key: 'iq', value: 'iq', flag: 'iq', text: 'Iquique' },
        { key: 'an', value: 'an', flag: 'an', text: 'Antofagasta' },
        { key: 'cl', value: 'cl', flag: 'cl', text: 'Calama' },
    ]

    */


    //const classes = useStyles();
    //const [opcion, setOpcion] = useState();

    /*
    const handleChange = (event) => {
        setOpcion(event.target.value)
    };
    */


    return(

        /*
        <form className={classes.root} noValidate autoComplete="off">
            <div>
                <TextField
                id="standar-select-text"
                select
                label="Select"
                value={opcion}
                onChange={handleChange}
                helperText="Please select your option"
            >
                {opciones.map((option) => (
                    <MenuItem key={option.value} value={option.value}>
                        {option.label}
                    </MenuItem>
                ))}
            </TextField>
            </div>

        </form>
        */
       /*
       { alerta ? ( <div className={`alerta ${alerta.categoria}`}>{alerta.msg} </div> ) : null }
       */

        <Fragment>
            <div className="form-usuario">

            <div className="contenedor-form sombra-dark">
                <h2>Obtener una Cuenta</h2>

                <form
                    onSubmit={onSubmit}
                >
                    <div className="campo-form">
                        <label htmlFor="rut">Rut</label>
                        <input
                            type="text"
                            id="rut"
                            name="rut"
                            placeholder="rut ej: 19000000-1"
                            value={rut}
                            onChange={onChange}
                        />
                    </div>

                    <div className="campo-form">
                        <label htmlFor="nombres">Nombres</label>
                        <input
                            type="text"
                            id="nombre"
                            name="nombre"
                            placeholder="Ingresa tus Nombres"
                            value={nombre}
                            onChange={onChange}
                        />
                    </div>

                    <div className="campo-form">
                        <label htmlFor="apellidos">Apellidos</label>
                        <input
                            type="text"
                            id="apellido"
                            name="apellido"
                            placeholder="Ingresa tus Apellidos"
                            value={apellido}
                            onChange={onChange}
                        />
                    </div>

                    <div className="campo-form">
                        <label htmlFor="ciudad">Ciudad</label>
                        <input
                            type="text"
                            id="ciudad"
                            name="ciudad"
                            placeholder="Ingresa tu Ciudad de Nacimiento"
                            value={ciudad}
                            onChange={onChange}
                        />
                    </div>

                    <div className="campo-form">
                        <label htmlFor="telefono">TelÃ©fono MÃ³vil</label>
                        <input
                            type="text"
                            id="telefono"
                            name="telefono"
                            placeholder="Ingresa tu MÃ³vil"
                            value={telefono}
                            onChange={onChange}
                        />
                    </div>

                    <div className="campo-form">
                        <label htmlFor="cargo">Cargo</label>
                        <input
                            type="text"
                            id="cargo"
                            name="cargo"
                            placeholder="Ingresa tu Cargo"
                            value={cargo}
                            onChange={onChange}
                        />
                    </div>

                    <div className="campo-form">
                        <label htmlFor="email">Email</label>
                        <input
                            type="email"
                            id="email"
                            name="email"
                            placeholder="Ingresa tu Email"
                            value={email}
                            onChange={onChange}
                        />
                    </div>

                    <div className="campo-form">
                        <label htmlFor="password">Password</label>
                        <input
                            type="password"
                            id="password"
                            name="password"
                            placeholder="Ingresa tu Password"
                            value={password}
                            onChange={onChange}
                        />
                    </div>

                    <div className="campo-form">
                        <label htmlFor="passwor">Password</label>
                        <input
                            type="password"
                            id="confirmar"
                            name="confirmar"
                            placeholder="Repite tu Password"
                            value={confirmar}
                            onChange={onChange}
                        />
                    </div>

                    <div className="campo-form">
                        <input type="submit" className="btn btn-primario btn-block"
                        value="Registrarme" />
                    </div>

                </form>

                <Link to={'/login'} className="enlace-cuenta">
                    Volver a Iniciar SesiÃ³n
                </Link>

            </div>
        </div>
        </Fragment>

    )};

export default Registro;
