import React, { useState } from 'react';
import { Link } from 'react-router-dom';

const Login = () => {

    //State para iniciar sesi贸n
    const [usuario, guardarUsuario] = useState({
        email:'',
        password: ''
    });

    //extraer de usuario
    const { email, password} = usuario;

    //aqui vamos a ir guardando en el State
    const onChange = e => {
        guardarUsuario({
            //traemos la copia de usuario para lo que este en el State no se 
            //sobre escriba en otra pieza de esta
            //para reescribir la actual -> [e.target.name] : e.target.value
            ...usuario,
            [e.target.name] : e.target.value
        });
    }

    //Cuando el usuario quiere iniciar sesion
    const onSubmit = e => {
        e.preventDefault();

        //Validar que no haya campos vacios


        //Pasarlo al action
    }

    

    return(
        <div className="form-usuario">
            <div className="contenedor-form sombra-dark">
                <h1>Iniciar Sesi贸n</h1>

                <form
                    onSubmit={onSubmit}
                >

                    

                    <div className="campo-form">
                        <label htmlFor="email">Email</label>
                        <input
                            type="email"
                            id="email"
                            name="email"
                            placeholder="Ingresa tu Email"
                            value={email}
                            onChange={onChange}
                        />
                    </div>

                    <div className="campo-form">
                        <label htmlFor="password">Password</label>
                        <input
                            type="password"
                            id="password"
                            name="password"
                            placeholder="Ingresa tu Password"
                            value={password}
                            onChange={onChange}
                        />
                    </div>

                    <div className="campo-form">
                        <input type="submit" className="btn btn-primario btn-block"
                        value="Iniciar Sesi贸n" />
                    </div>

                </form>
               
                <Link to={'/registro'} className="enlace-cuenta">
                    Obtener Cuenta
                </Link>

            </div>
        </div>
    )};

export default Login;
